import { Box, Image } from "@chakra-ui/react";
import { useState, useEffect } from "react";

export default function ImageSlider() {
    let images=['data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSbruNEeVrRVtQLUdYtuybdKXkpXw2JGg_6ag&s',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ52QMU3JBN6Qq1iSGdG6jw9BgPp97MTTxXaw&s',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,'
    ]
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const intervalId = setInterval(() => {
            setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 3000); // 3 seconds interval

        return () => clearInterval(intervalId); // Cleanup interval on component unmount
    }, [images.length]);

    return (
        <Box borderBottom={'2px solid white'} boxShadow={'xl'} m='-10px'  position="relative" width="100vw" height="210px" overflow="hidden">
            {images.map((image, index) => (
                <Image
                    key={index}
                    src={image}
                    alt={`Slide ${index}`}
                    opacity={index === currentIndex ? 1 : 0}
                    transition="opacity 0.5s ease-in-out"
                    position="absolute"
                    top={0}
                    left={0}
                    width="100%"
                    height="100%"
                    objectFit="cover"
                />
            ))}
        </Box>
    );
}

